// Post-process the rich content to add interactive callbacks
const richContent = $input.first().json;

// Add callback handling for interactive elements
if (richContent.type === 'rich' && richContent.content) {
  // Enhance script with callback handling
  if (richContent.content.script) {
    richContent.content.script += `

      // Enhanced callback system
      window.addEventListener('message', (event) => {
        if (event.data.type === 'chat-action') {
          console.log('Chat action received:', event.data);

          // Handle different actions
          switch (event.data.action) {
            case 'refresh-dashboard':
              // Simulate dashboard refresh
              const metrics = container.querySelectorAll('.metric-value');
              metrics.forEach(metric => {
                const current = parseInt(metric.textContent.replace(/[^0-9]/g, ''));
                const newValue = current + Math.floor(Math.random() * 100);
                metric.textContent = metric.textContent.replace(/[0-9,]+/, newValue.toLocaleString());
              });
              break;

            case 'export-data':
              // Simulate data export
              const data = {
                timestamp: new Date().toISOString(),
                metrics: Array.from(container.querySelectorAll('.metric-card')).map(card => ({
                  label: card.querySelector('.metric-label').textContent,
                  value: card.querySelector('.metric-value').textContent
                }))
              };
              console.log('Exported data:', data);
              alert('Data exported to console!');
              break;

