// AI-powered campaign performance analysis
const items = $input.all();
const analyzedItems = [];

for (const item of items) {
  const data = item.json;
  let performanceScore = 0;
  let insights = [];
  let recommendations = [];
  let alertLevel = 'normal';

  // Extract metrics
  const clicks = data.metrics?.clicks || 0;
  const impressions = data.metrics?.impressions || 0;
  const cost = (data.metrics?.cost_micros || 0) / 1000000;
  const conversions = data.metrics?.conversions || 0;
  const ctr = impressions > 0 ? (clicks / impressions) * 100 : 0;
  const conversionRate = clicks > 0 ? (conversions / clicks) * 100 : 0;
  const costPerConversion = conversions > 0 ? cost / conversions : 0;
  const avgCpc = clicks > 0 ? cost / clicks : 0;

  // CTR Analysis (Click-Through Rate)
  if (ctr >= 5) {
    performanceScore += 30;
    insights.push(`Excellent CTR: ${ctr.toFixed(2)}% (+30 points)`);
  } else if (ctr >= 3) {
    performanceScore += 20;
    insights.push(`Good CTR: ${ctr.toFixed(2)}% (+20 points)`);
  } else if (ctr >= 1.5) {
    performanceScore += 10;
    insights.push(`Average CTR: ${ctr.toFixed(2)}% (+10 points)`);
    recommendations.push('Consider A/B testing ad copy to improve CTR');
  } else {
    insights.push(`Low CTR: ${ctr.toFixed(2)}% (0 points)`);
    recommendations.push('URGENT: Review ad relevance and targeting');
    alertLevel = 'warning';
  }

  // Conversion Rate Analysis
  if (conversionRate >= 10) {
    performanceScore += 35;
    insights.push(`Outstanding conversion rate: ${conversionRate.toFixed(2)}% (+35 points)`);
  } else if (conversionRate >= 5) {
    performanceScore += 25;
    insights.push(`Strong conversion rate: ${conversionRate.toFixed(2)}% (+25 points)`);
  } else if (conversionRate >= 2) {
    performanceScore += 15;
    insights.push(`Moderate conversion rate: ${conversionRate.toFixed(2)}% (+15 points)`);
    recommendations.push('Optimize landing pages for better conversions');
  } else {
    insights.push(`Low conversion rate: ${conversionRate.toFixed(2)}% (0 points)`);
    recommendations.push('CRITICAL: Review landing page experience and targeting');
    alertLevel = 'critical';
  }

  // Cost Efficiency Analysis
  if (costPerConversion > 0) {
    if (costPerConversion <= 25) {
      performanceScore += 25;
      insights.push(`Excellent cost per conversion: $${costPerConversion.toFixed(2)} (+25 points)`);
    } else if (costPerConversion <= 50) {
      performanceScore += 15;
      insights.push(`Good cost per conversion: $${costPerConversion.toFixed(2)} (+15 points)`);
    } else if (costPerConversion <= 100) {
      performanceScore += 8;
      insights.push(`Fair cost per conversion: $${costPerConversion.toFixed(2)} (+8 points)`);
      recommendations.push('Look for ways to reduce cost per conversion');
    } else {
      insights.push(`High cost per conversion: $${costPerConversion.toFixed(2)} (0 points)`);
      recommendations.push('URGENT: Review bid strategy and audience targeting');
      alertLevel = alertLevel === 'critical' ? 'critical' : 'warning';
    }
  }

  // Volume Analysis
  if (clicks >= 1000) {
    performanceScore += 10;
    insights.push(`High traffic volume: ${clicks} clicks (+10 points)`);
  } else if (clicks >= 500) {
    performanceScore += 5;
    insights.push(`Good traffic volume: ${clicks} clicks (+5 points)`);
  } else if (clicks < 100) {
    insights.push(`Low traffic volume: ${clicks} clicks (0 points)`);
    recommendations.push('Consider increasing budget or expanding targeting');
  }

  // Determine performance tier
  let performanceTier = 'Underperforming';
  if (performanceScore >= 75) {
    performanceTier = 'Excellent';
  } else if (performanceScore >= 55) {
    performanceTier = 'Good';
  } else if (performanceScore >= 35) {
    performanceTier = 'Fair';
  }

  // Add strategic recommendations
  if (performanceTier === 'Excellent') {
    recommendations.push('Scale this campaign - increase budget by 20-30%');
    recommendations.push('Use this as a template for new campaigns');
  } else if (performanceTier === 'Good') {
    recommendations.push('Test incremental budget increases');
    recommendations.push('Identify top-performing ad groups to scale');
  }

  analyzedItems.push({
    json: {
      ...data,
      campaignId: data.campaign?.id,
      campaignName: data.campaign?.name,
      campaignStatus: data.campaign?.status,
      performanceScore: performanceScore,
      performanceTier: performanceTier,
      alertLevel: alertLevel,
      insights: insights,
      recommendations: recommendations,
      metrics: {
        clicks: clicks,
        impressions: impressions,
        cost: cost.toFixed(2),
        conversions: conversions,
        ctr: ctr.toFixed(2),
        conversionRate: conversionRate.toFixed(2),
        costPerConversion: costPerConversion.toFixed(2),
        avgCpc: avgCpc.toFixed(2)
      },
      analyzedAt: new Date().toISOString()
    }
  });
}

return analyzedItems;

