// Merge inventory and sales data
const inventoryData = $input.first().json;
const salesData = $input.last().json;

// Create a map of sales data by product_id
const salesMap = {};
if (Array.isArray(salesData)) {
  salesData.forEach(sale => {
    salesMap[sale.product_id] = sale;
  });
} else {
  salesMap[salesData.product_id] = salesData;
}

// Merge data
const mergedData = [];
const inventory = Array.isArray(inventoryData) ? inventoryData : [inventoryData];

inventory.forEach(item => {
  const salesInfo = salesMap[item.product_id] || {
    units_sold_30days: 0,
    avg_daily_sales: 0,
    trend: 'stable'
  };

  mergedData.push({
    product_id: item.product_id,
    product_name: item.product_name,
    current_stock: item.current_stock,
    reorder_point: item.reorder_point,
    supplier_id: item.supplier_id,
    units_sold_30days: salesInfo.units_sold_30days,
    avg_daily_sales: salesInfo.avg_daily_sales,
    trend: salesInfo.trend,
    unit_cost: item.unit_cost || 0,
    lead_time_days: item.lead_time_days || 7
  });
});

return mergedData.map(item => ({ json: item }));

// Parse AI response and prepare data for filtering
const item = $input.first().json;

let aiResponse;
try {
  // Try to parse the AI response
  const content = item.message?.content || item.content || '{}';
  aiResponse = typeof content === 'string' ? JSON.parse(content) : content;
} catch (error) {
  // If parsing fails, use defaults
  aiResponse = {
    should_reorder: false,
    recommended_quantity: 0,
    days_until_stockout: 999,
    forecasted_demand_30days: 0,
    confidence_level: 'low',
    reasoning: 'Failed to parse AI response'
  };
}

// Combine original data with AI forecast
const result = {
  ...item,
  ai_forecast: aiResponse,
  should_reorder: aiResponse.should_reorder,
  recommended_quantity: aiResponse.recommended_quantity,
  days_until_stockout: aiResponse.days_until_stockout,
  forecasted_demand_30days: aiResponse.forecasted_demand_30days,
  confidence_level: aiResponse.confidence_level,
  reasoning: aiResponse.reasoning
};

return { json: result };

// Generate Purchase Order
const items = $input.all();

const purchaseOrders = items.map((item, index) => {
  const data = item.json;

  // Generate unique PO number
  const poNumber = `PO-${Date.now()}-${String(index + 1).padStart(3, '0')}`;

  // Calculate order details
  const quantity = data.recommended_quantity;
  const unitCost = data.unit_cost || 0;
  const totalCost = quantity * unitCost;

  // Create purchase order
  const purchaseOrder = {
    po_number: poNumber,
    product_id: data.product_id,
    product_name: data.product_name,
    supplier_id: data.supplier_id,
    quantity: quantity,
    unit_cost: unitCost,
    total_cost: totalCost,
    currency: 'USD',
    order_date: new Date().toISOString(),
    expected_delivery: new Date(Date.now() + (data.lead_time_days * 24 * 60 * 60 * 1000)).toISOString(),
    status: 'pending',
    created_by: 'AI_System',
    notes: data.reasoning,
    forecast_data: {
      current_stock: data.current_stock,
      days_until_stockout: data.days_until_stockout,
      forecasted_demand_30days: data.forecasted_demand_30days,
      confidence_level: data.confidence_level
    }
  };

  return { json: purchaseOrder };
});

return purchaseOrders;
