={{
  (() => {
    const names = [];

    // Get source nodes (keys)
    names.push(...Object.keys($('Extract Workflow Nodes Connections').item.json.connections));

    // Get target nodes
    for (const conn of Object.values($('Extract Workflow Nodes Connections').item.json.connections)) {
      for (const type of Object.values(conn)) {          // main, error, etc.
        for (const outputArray of type) {                // [[...]]
          for (const target of outputArray) {            // [{node: "..."}]
            if (target.node) names.push(target.node);
          }
        }
      }
    }

    return names;
  })()
}}

={{
  (() => {
    const allNodeNames = $('Extract Workflow Nodes Connections').item.json.nodes.map(node => node.name);
    const nodesString = JSON.stringify($('Extract Workflow Nodes Connections').item.json.nodes);
    const foundPatterns = [];

    allNodeNames.forEach(nodeName => {
      const patterns = [
        `$('${nodeName}')`,
        `$("${nodeName}")`
      ];

      patterns.forEach(pattern => {
        if (nodesString.includes(pattern)) {
          foundPatterns.push(pattern);
        }
      });
    });

    return foundPatterns;
  })()
}}

={{ $json.old_workflow.nodes.map(node => {
  // Find mapping for the node name
  const mapping = $json.AI_output.find(item => item.old_name === node.name);

  // Update parameters by replacing all node name references
  let updatedParameters = JSON.stringify(node.parameters);
  $json.AI_output.forEach(aiMapping => {
    const pattern = new RegExp(aiMapping.old_name.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'g');
    updatedParameters = updatedParameters.replace(pattern, aiMapping.new_name);
  });

  return {
    ...node,
    name: mapping ? mapping.new_name : node.name,
    parameters: JSON.parse(updatedParameters)
  };
}) }}

={{
  (() => {
    const newConnections = {};

    for (const [sourceNode, connections] of Object.entries($json.old_workflow.connections)) {
      // Find mapping for the source node (key)
      const sourceMapping = $json.AI_output.find(item => item.old_name === sourceNode);
      const newSourceNode = sourceMapping ? sourceMapping.new_name : sourceNode;

      // Deep copy and update target nodes
      const updatedConnections = {};
      for (const [connectionType, connectionArrays] of Object.entries(connections)) {
        updatedConnections[connectionType] = connectionArrays.map(connArray => 
          connArray.map(conn => {
            const targetMapping = $json.AI_output.find(item => item.old_name === conn.node);
            return {
              ...conn,
              node: targetMapping ? targetMapping.new_name : conn.node
            };
          })
        );
      }

      newConnections[newSourceNode] = updatedConnections;
    }

    return newConnections;
  })()
}}

