// Funzione ricorsiva per cercare file_id in oggetti nested
function findFileId(obj, visited = new Set()) {
  // Evita loop infiniti con riferimenti circolari
  if (visited.has(obj)) return null;

  if (obj && typeof obj === 'object') {
    visited.add(obj);

    // Controlla se l'oggetto corrente ha file_id
    if (obj.file_id) return obj.file_id;
    if (obj.fileId) return obj.fileId;
    if (obj.id && typeof obj.id === 'string' && obj.id.startsWith('file-')) return obj.id;

    // Cerca in tutti i valori dell'oggetto
    for (const key in obj) {
      if (obj.hasOwnProperty(key)) {
        const result = findFileId(obj[key], visited);
        if (result) return result;
      }
    }
  }

  return null;
}

// Estrai file_id da ogni item
const output = [];

for (const item of $input.all()) {
  let fileId = null;

  // Strategia 1: Controlla posizioni comuni
  if (item.json.file_id) {
    fileId = item.json.file_id;
  } 
  else if (item.json.data?.file_id) {
    fileId = item.json.data.file_id;
  }
  else if (item.json.result?.file_id) {
    fileId = item.json.result.file_id;
  }
  else if (item.json.response?.file_id) {
    fileId = item.json.response.file_id;
  }
  else if (item.json.output?.file_id) {
    fileId = item.json.output.file_id;
  }
  // Cerca in attachments array
  else if (Array.isArray(item.json.attachments) && item.json.attachments.length > 0) {
    fileId = item.json.attachments[0].file_id || item.json.attachments[0].id;
  }
  // Cerca in files array
  else if (Array.isArray(item.json.files) && item.json.files.length > 0) {
    fileId = item.json.files[0].file_id || item.json.files[0].id;
  }
  // Strategia 2: Ricerca ricorsiva in tutto l'oggetto
  else {
    fileId = findFileId(item.json);
  }

  // Strategia 3: Cerca nel binary data
  if (!fileId && item.binary) {
    for (const binaryKey in item.binary) {
      if (item.binary[binaryKey].id) {
        fileId = item.binary[binaryKey].id;
        break;
      }
    }
  }

  output.push({
    json: {
      file_id: fileId
    }
  });
}

return output;

