// LINE Webhook parsing
const body = items[0].json.body;

if (!body.events || !Array.isArray(body.events) || !body.events[0]) {
  throw new Error("No LINE events found");
}

const event = body.events[0];

return [
  {
    json: {
      replyToken: event.replyToken,
      userId: event.source.userId,
      message: event.message.text,
      timestamp: event.timestamp
    }
  }
];

// Parse response and extract image URL
const response = items[0].json;
const data = Array.isArray(response) ? response[0] : response;

if (data.code === 200 && data.data) {
  const taskData = data.data;

  if (taskData.state === 'success') {
    let imageUrl = null;
    try {
      const resultData = JSON.parse(taskData.resultJson);
      const resultUrls = resultData.resultUrls || [];
      if (resultUrls.length > 0) {
        imageUrl = resultUrls[0];
      }
    } catch (e) {
      throw new Error('Failed to parse resultJson');
    }

    if (imageUrl) {
      return [{
        json: {
          imageUrl: imageUrl,
          taskId: taskData.taskId,
          status: 'completed'
        }
      }];
    }
  } else if (taskData.state === 'fail') {
    throw new Error(`Image generation failed: ${taskData.failMsg}`);
  } else {
    // Loop back if processing
    return [{
        json: { status: 'processing' }
    }];
  }
}

// Extract the actual prompt text from Gemini's response
const response = items[0].json;

let promptText = "";

// Check if we have the content structure from Gemini
if (response.content && response.content.parts && response.content.parts[0]) {
  let text = response.content.parts[0].text;

  // Parse the JSON array if it's a string
  try {
    const parsed = JSON.parse(text);
    // If it's an array, get the first element
    if (Array.isArray(parsed) && parsed.length > 0) {
      promptText = parsed[0];
    } else if (typeof parsed === 'string') {
      promptText = parsed;
    } else {
      promptText = text; // Use as is if not parseable
    }
  } catch (e) {
    // If JSON parsing fails, clean the text manually
    promptText = text
      .replace(/^\[|\]$/g, '') // Remove brackets
      .replace(/^["']|["']$/g, '') // Remove quotes
      .replace(/\\n/g, '') // Remove newlines
      .trim();
  }
}

// Clean any remaining escape characters
promptText = promptText.replace(/\\"/g, '"').trim();

return [{
  json: {
    prompt: promptText,
    userId: $('Extract Data').item.json.userId,
    taskId: response.taskId || null
  }
}];
