// Take the output from the LLM - News Summarizer node
const item = items[0].json;

// 1. Get raw text from Gemini format
let text = item?.content?.parts?.[0]?.text;
if (!text) {
  throw new Error(
    "Unexpected LLM output, missing content.parts[0].text:\n" +
    JSON.stringify(item, null, 2)
  );
}

// 2. If there is ```json ... ``` remove the fences
const fenced = text.match(/```json?[\s\n]*([\s\S]*?)```/i);
if (fenced) {
  text = fenced[1].trim();
}

// 3. Extract the first { ... } block (in case the model adds text before/after)
let candidate = text;
const start = candidate.indexOf("{");
const end = candidate.lastIndexOf("}");
if (start === -1 || end === -1) {
  throw new Error(
    "No JSON object found in LLM output:\n" + text
  );
}
candidate = candidate.slice(start, end + 1);

// 4. Clean trailing commas before } or ]
candidate = candidate.replace(/,\s*([}\]])/g, "$1");

// 5. Parse JSON
let data;
try {
  data = JSON.parse(candidate);
} catch (err) {
  throw new Error(
    "JSON parse error (inner payload from LLM):\n" +
    err.message +
    "\n\nPayload was:\n" +
    candidate
  );
}

// 6. Special cases: { "text": "{...}" } etc.
if (data.text && (!data.subject || !data.html)) {
  try {
    const inner = JSON.parse(data.text);
    if (inner.subject && inner.html) {
      data = inner;
    }
  } catch {
    // if it fails, keep data as-is and proceed
  }
}

// 7. Final validation
if (!data.subject || !data.html) {
  throw new Error(
    "LLM JSON does not contain subject/html as expected:\n" +
    JSON.stringify(data, null, 2)
  );
}

// 8. HTML email template
const htmlEmail = `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>${data.subject}</title>
  <style>
    body { font-family: Arial, sans-serif; line-height:1.5; color:#333; background-color:#f7f9fa; margin:0; padding:20px; }
    .container { max-width:700px; margin:0 auto; background:#fff; border-radius:8px; box-shadow:0 2px 8px rgba(0,0,0,0.1); overflow:hidden; }
    .header { background:#2c3e50; color:#fff; padding:20px; text-align:center; }
    .header h1 { margin:0; font-size:24px; }
    .content { padding:20px; }
    h2 { color:#e74c3c; border-bottom:2px solid #e74c3c; padding-bottom:5px; }
    ul { padding-left:20px; }
    li { margin-bottom:10px; }
    a { color:#2980b9; text-decoration:none; }
    a:hover { text-decoration:underline; }
    .footer { background:#ecf0f1; text-align:center; padding:10px; font-size:12px; color:#7f8c8d; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>${data.subject}</h1>
    </div>
    <div class="content">
      ${data.html}
    </div>
    <div class="footer">
      <em>Briefing automatically sent on ${new Date().toLocaleDateString('en-US', {
        year: 'numeric', month: 'long', day: 'numeric'
      })}.</em>
    </div>
  </div>
</body>
</html>
`.trim();

// 9. Return data for the Gmail node
return [
  {
    json: {
      subject: data.subject,
      html: htmlEmail,
    },
  },
];

