// Function to clean HTML entities from text
function cleanText(text) {
  if (!text) return "";
  return text
    .replace(/<[^>]*>/g, '')
    .replace(/&hellip;/g, '...')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/&#8211;/g, '–')
    .replace(/&#8212;/g, '—')
    .replace(/&#(\d+);/g, (_match, dec) => String.fromCharCode(dec))
    .replace(/\n/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();
}

// Calculate reading time based on word count
function calculateReadingTime(text) {
  const words = text.split(' ').length;
  const readingTime = 2 + (words * 0.3);
  return Math.min(Math.max(readingTime, 3), 7);
}

// Get today's date in numeric format DD-MM-YYYY
function getDateFormat() {
  const date = new Date();
  const day = String(date.getDate()).padStart(2, '0');
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const year = date.getFullYear();
  return `${day}-${month}-${year}`;
}

// Variables
const logo_big = $('Config Variables').first().json.logo_big;
const logo_small = $('Config Variables').first().json.logo_small;
const daily_digest_text = $('Config Variables').first().json.daily_digest_text;
const button_text = $('Config Variables').first().json.button_text;
const button_bg_color = $('Config Variables').first().json.button_bg_color;

// Get all items from previous node
const allItems = $input.all();

// Transform each item to include video data
const transformedItems = allItems.map(item => {
  const n = item.json;
  const title = n.title || "";
  const photo_url = n.photo_url || "";
  const video = n.video || null;

  return {
    clip: {
      asset: {
        type: "image",
        src: photo_url
      },
      length: 4.5,
      transition: { in: "fade", out: "fade" }
    },
    overlay: {
      title: title
    },
    video: video
  };
});

// Build Shotstack payload
const shotstackClips = [];
const introBgClips = [];
const introLogoClips = [];
const introTitleClips = [];
const introDateClips = [];
const paginationBgClips = [];
const paginationTextClips = [];
const buttonBgClips = [];
const buttonTextClips = [];
let currentTime = 0;

// 1. INTRO SLIDE
const introLength = 3;
introBgClips.push({
  asset: {
    type: "shape",
    shape: "rectangle",
    fill: { color: "#000000", opacity: 1 },
    stroke: { color: "#000000", width: "0" },
    width: 1080,
    height: 1920,
    rectangle: { width: 1080, height: 1920, cornerRadius: 0 }
  },
  start: 0,
  length: introLength,
  position: "center"
});

introLogoClips.push({
  asset: { type: "image", src: logo_big },
  start: 0,
  length: introLength,
  position: "center",
  offset: { x: 0, y: -0.1 },
  scale: 0.05
});

introTitleClips.push({
  asset: {
    type: "text",
    text: daily_digest_text,
    font: { color: "#ffffff", family: "Montserrat ExtraBold", size: "62", lineHeight: 1.2 },
    alignment: { horizontal: "center", vertical: "center" },
    width: 800,
    height: 150
  },
  start: 0,
  length: introLength,
  position: "center",
  offset: { x: 0, y: 0.05 }
});

introDateClips.push({
  asset: {
    type: "text",
    text: getDateFormat(),
    font: { color: "#cccccc", family: "Montserrat ExtraBold", size: "28", lineHeight: 1.2 },
    alignment: { horizontal: "center", vertical: "center" },
    width: 800,
    height: 200
  },
  start: 0,
  length: introLength,
  position: "center",
  offset: { x: 0, y: 0.1 }
});

currentTime += introLength;

// 2. NEWS SLIDES
transformedItems.forEach((item, index) => {
  const cleanedTitle = cleanText(item.overlay.title);
  const duration = calculateReadingTime(cleanedTitle);
  const baseStart = currentTime;
  const hasVideo = item.video && item.video.src;

  // Background media (video or image)
  shotstackClips.push({
    asset: {
      type: hasVideo ? "video" : "image",
      src: hasVideo ? item.video.src : item.clip.asset.src,
      ...(hasVideo && { volume: 0 })
    },
    start: baseStart,
    length: duration,
    fit: "crop",
    position: "center",
    transition: { in: "fade", out: "fade" }
  });

  // Dark overlay
  shotstackClips.push({
    asset: {
      type: "shape",
      shape: "rectangle",
      fill: { color: "#000000", opacity: 0.4 },
      stroke: { color: "#000000", width: "0" },
      width: 1080,
      height: 1920,
      rectangle: { width: 1080, height: 1920, cornerRadius: 0 }
    },
    start: baseStart,
    length: duration,
    offset: { x: 0, y: 0 },
    position: "center"
  });

  // Title
  shotstackClips.push({
    asset: {
      type: "text",
      text: cleanedTitle,
      alignment: { horizontal: "center", vertical: "top" },
      font: { color: "#ffffff", family: "Montserrat ExtraBold", size: "62", lineHeight: 1.2 },
      width: 800,
      height: 800
    },
    start: baseStart + 0.2,
    length: duration - 0.4,
    position: "center",
    offset: { x: 0, y: 0.1 }
  });

  // Logo
  shotstackClips.push({
    asset: { type: "image", src: logo_small },
    start: baseStart,
    length: duration,
    position: "topRight",
    scale: 0.07,
    offset: { x: -0.05, y: -0.05 }
  });

  // Pagination background
  paginationBgClips.push({
    asset: {
      type: "shape",
      shape: "rectangle",
      fill: { color: "#ffffff", opacity: 0.95 },
      stroke: { color: "#000000", width: "0" },
      width: 140,
      height: 60,
      rectangle: { width: 140, height: 60, cornerRadius: 25 }
    },
    start: baseStart + 0.2,
    length: duration - 0.4,
    position: "bottomRight",
    offset: { x: -0.08, y: 0.08 }
  });

  // Pagination text
  paginationTextClips.push({
    asset: {
      type: "text",
      text: `${index + 1} / ${transformedItems.length}`,
      font: { color: "#333333", family: "Montserrat", size: "22", weight: "700" },
      alignment: { horizontal: "center", vertical: "center" },
      width: 140,
      height: 60
    },
    start: baseStart + 0.2,
    length: duration - 0.4,
    position: "bottomRight",
    offset: { x: -0.08, y: 0.08 }
  });

  // Button background
  buttonBgClips.push({
    asset: {
      type: "shape",
      shape: "rectangle",
      fill: { color: button_bg_color, opacity: 1 },
      stroke: { color: button_bg_color, width: "0" },
      width: 400,
      height: 70,
      rectangle: { width: 400, height: 70, cornerRadius: 35 }
    },
    start: baseStart + 0.2,
    length: duration - 0.4,
    position: "center",
    offset: { x: 0, y: -0.15 }
  });

  // Button text
  buttonTextClips.push({
    asset: {
      type: "text",
      text: button_text,
      font: { color: "#ffffff", family: "Montserrat ExtraBold", size: "32" },
      alignment: { horizontal: "center", vertical: "center" },
      width: 400,
      height: 70
    },
    start: baseStart + 0.2,
    length: duration - 0.4,
    position: "center",
    offset: { x: 0, y: -0.15 }
  });

  currentTime += duration;
});

// 3. OUTRO SLIDE
const outroLength = 3;
introBgClips.push({
  asset: {
    type: "shape",
    shape: "rectangle",
    fill: { color: "#000000", opacity: 1 },
    stroke: { color: "#000000", width: "0" },
    width: 1080,
    height: 1920,
    rectangle: { width: 1080, height: 1920, cornerRadius: 0 }
  },
  start: currentTime,
  length: outroLength,
  transition: { in: "fade" },
  position: "center"
});

introLogoClips.push({
  asset: { type: "image", src: logo_big },
  start: currentTime,
  length: outroLength,
  position: "center",
  offset: { x: 0, y: -0.1 },
  scale: 0.05,
  transition: { in: "fade" }
});

introTitleClips.push({
  asset: {
    type: "text",
    text: daily_digest_text,
    font: { color: "#ffffff", family: "Montserrat ExtraBold", size: "56", lineHeight: 1.1 },
    alignment: { horizontal: "center", vertical: "center" },
    width: 800,
    height: 200
  },
  start: currentTime,
  length: outroLength,
  position: "center",
  offset: { x: 0, y: 0.05 },
  transition: { in: "fade" }
});

introDateClips.push({
  asset: {
    type: "text",
    text: getDateFormat(),
    font: { color: "#cccccc", family: "Montserrat ExtraBold", size: "28", lineHeight: 1.2 },
    alignment: { horizontal: "center", vertical: "center" },
    width: 800,
    height: 200
  },
  start: currentTime,
  length: outroLength,
  position: "center",
  offset: { x: 0, y: 0.1 },
  transition: { in: "fade" }
});

// Build final payload
const shotstackPayload = {
  timeline: {
    background: "#000000",
    tracks: [
      { clips: buttonTextClips },
      { clips: buttonBgClips },
      { clips: paginationTextClips },
      { clips: paginationBgClips },
      { clips: introLogoClips },
      { clips: introTitleClips },
      { clips: introDateClips },
      { clips: shotstackClips },
      { clips: introBgClips }
    ]
  },
  output: {
    format: "mp4",
    size: { width: 1080, height: 1920 },
    fps: 25
  }
};

// Add audio
const totalDuration = currentTime + outroLength;
const audioLength = 30;
const loopsNeeded = Math.ceil(totalDuration / audioLength);
const audioTrack = { clips: [] };

for (let i = 0; i < loopsNeeded; i++) {
  const audioStart = i * audioLength;
  const audioClipLength = Math.min(audioLength, totalDuration - audioStart);
  audioTrack.clips.push({
    asset: {
      type: "audio",
      src: $('Config Variables').first().json.sound_url,
      volume: 0.6
    },
    start: audioStart,
    length: audioClipLength
  });
}

shotstackPayload.timeline.tracks.push(audioTrack);

return [{ json: shotstackPayload }];

function extractVideoUrl(contentHtml) {
  if (!contentHtml) return null;
  try {
    const scriptMatch = contentHtml.match(/<script type="application\/json" class="wp-playlist-script">(.*?)<\/script>/s);
    if (scriptMatch && scriptMatch[1]) {
      const playlistData = JSON.parse(scriptMatch[1]);
      if (playlistData.tracks && playlistData.tracks.length > 0) {
        return playlistData.tracks[0].src;
      }
    }
    const videoSrcMatch = contentHtml.match(/<video[^>]*src="([^"]+)"/);
    if (videoSrcMatch && videoSrcMatch[1]) return videoSrcMatch[1];

    const sourceMatch = contentHtml.match(/<source[^>]*src="([^"]+)"/);
    if (sourceMatch && sourceMatch[1]) return sourceMatch[1];
  } catch (error) {
    return null;
  }
  return null;
}

// Process WordPress articles
const articles = $input.all();
return articles.map(article => {
  const content = article.json.content?.rendered;
  const videoUrl = extractVideoUrl(content);

  return {
    json: {
      ...article.json,
      video: videoUrl ? { src: videoUrl } : null
    }
  };
});

