// Parse the AI output
// If your AI returns an array like in your example, we need to access the first item
let aiData;

try {
  // Check if the input is already an object or needs parsing
  if (typeof $input.all()[0].json.output === 'string') {
    aiData = JSON.parse($input.all()[0].json.output);
  } else {
    // If it's already an object, use it directly
    aiData = $input.all()[0].json.output;
  }
} catch (error) {
  throw new Error('Failed to parse AI output: ' + error.message);
}

// Extract the components
const postContent = aiData.post_content || '';
const hashtags = aiData.hashtags || [];
const engagementTip = aiData.engagement_tip || '';
const callToAction = aiData.call_to_action || '';
const emojiSuggestions = aiData.emoji_suggestions || '';

// Format hashtags as a single string
const hashtagString = hashtags.join(' ');

// Create the final formatted post
// Post content + blank line + hashtags
const finalPost = `${postContent}\n\n${hashtagString}`;

// Calculate character count
const characterCount = finalPost.length;

// Calculate word count (bonus metric)
const wordCount = finalPost.trim().split(/\s+/).length;

// Check if post is within LinkedIn limits
const isWithinLimit = characterCount <= 3000;
const hasHashtags = hashtags.length >= 3;

// Return formatted output
return {
  // The final post ready to publish
  final_post: finalPost,

  // Original components (useful for display/editing)
  post_content: postContent,
  hashtags: hashtags,
  hashtag_string: hashtagString,

  // Metadata
  character_count: characterCount,
  word_count: wordCount,
  hashtag_count: hashtags.length,

  // Quality checks (for next node)
  is_within_limit: isWithinLimit,
  has_minimum_hashtags: hasHashtags,
  passes_quality_check: isWithinLimit && hasHashtags,

  // Additional info from AI
  engagement_tip: engagementTip,
  call_to_action: callToAction,
  emoji_suggestions: emojiSuggestions,

  // Timestamp
  generated_at: new Date().toISOString(),

  // Warning messages (if any)
  warnings: [
    !isWithinLimit ? `⚠️ Post exceeds 3000 characters (${characterCount} chars)` : null,
    !hasHashtags ? `⚠️ Post has fewer than 3 hashtags (${hashtags.length} found)` : null
  ].filter(Boolean)
};

// Get the image link
let imageUrl = $('Limit to One Post').first().json['Image link for your post'];

// Clean the URL
imageUrl = imageUrl.trim();

// Convert common sharing links to direct download links

// Google Drive
if (imageUrl.includes('drive.google.com/file/d/')) {
  const fileIdMatch = imageUrl.match(/\/d\/([^\/]+)/);
  if (fileIdMatch) {
    imageUrl = `https://drive.google.com/uc?export=download&id=${fileIdMatch[1]}`;
  }
}

// Dropbox - change dl=0 to dl=1
if (imageUrl.includes('dropbox.com') && imageUrl.includes('dl=0')) {
  imageUrl = imageUrl.replace('dl=0', 'dl=1');
}

// Imgur - convert to direct image link
if (imageUrl.includes('imgur.com/') && !imageUrl.includes('i.imgur.com')) {
  imageUrl = imageUrl.replace('imgur.com/', 'i.imgur.com/') + '.jpg';
}

// OneDrive/SharePoint
if (imageUrl.includes('sharepoint.com') || imageUrl.includes('1drv.ms')) {
  // OneDrive links need special handling - append ?download=1
  imageUrl = imageUrl.split('?')[0] + '?download=1';
}

return {
  json: {
    original_url: $('Limit to One Post').first().json['Image link for your post'],
    direct_download_url: imageUrl
  }
};

// Get data from previous n8n node
const inputData = $input.all();

// Get the JSON data from the first item
const jsonData = inputData[0].json;

// Navigate to the results array
const results = jsonData.results;

// Create output object
const output = {
  results: {}
};

// Process each result
results.forEach((item, index) => {
  output.results[`results[${index}]`] = {
    download: item.links.download,
    alt_description: item.alt_description,
    description: item.description
  };
});

// Return the data for n8n
return [{ json: output }];
