// Function to split the data into chunks of approximately 1000 characters
function splitDataIntoChunks(data) {
    const chunks = [];
    let currentChunk = "";

    data.forEach(item => {
        // Ensure that each item has a 'message' property
        if (item && item.message) {
            const message = item.message + "\n"; // Adding a newline for separation
            // Check if adding this message to the current chunk would exceed the 1000 characters limit
            if (currentChunk.length + message.length > 1000) {
                // If so, push the current chunk to the chunks array and start a new chunk
                chunks.push({ json: { data: currentChunk } });
                currentChunk = message;
            } else {
                // Otherwise, add the message to the current chunk
                currentChunk += message;
            }
        }
    });

    // Add the last chunk if it's not empty
    if (currentChunk) {
        chunks.push({ json: { data: currentChunk } });
    }

    return chunks;
}

// The input data is passed from the previous node
const inputData = items[0].json.data; // Accessing the 'data' property

// Process the data
const result = splitDataIntoChunks(inputData);

// Output the result
return result;
