parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "title": {
      "type": "string",
      "description": "Creative, meaningful title for the video - short, concise and matching the content",
      "minLength": 5,
      "maxLength": 80
    },
    "prompt": {
      "type": "string",
      "description": "Detailed VEO3 prompt with Subject, Context, Action, Style, Camera Motion, Composition, Ambiance and Audio elements",
      "minLength": 50,
      "maxLength": 2000
    },
    "aspect_ratio": {
      "type": "string",
      "enum": ["16:9"],
      "description": "Video aspect ratio - currently only 16:9 Landscape/Cinema supported"
    },
    "duration": {
      "type": "string",
      "enum": ["8s"],
      "description": "Video duration in seconds - currently only 8s supported"
    }
  },
  "required": ["title", "prompt", "aspect_ratio", "duration"],
  "additionalProperties": false
}
