/* N8N Landing Page CSS - Correct Selectors Based on HTML */

/* Style the main form card */
.card {
	position: relative;
	max-width: 500px;
	margin: 20px auto;
	padding: 20px;
	background: white !important;
	border-radius: 12px;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
	font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

/* Add custom header with gradient */
.card:before {
	content: '';
	display: block;
	height: 50px;
	background: linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%);
	position: relative;
	margin: -20px -20px 30px -20px;
	border-radius: 12px 12px 0 0;
}

/* Style the form header */
.form-header {
	text-align: center !important;
	color: #1f2937 !important;
	font-weight: 700 !important;
	margin: 80px 0 15px 0 !important;
	font-size: 2.2em !important;
	position: relative;
	z-index: 5;
}

/* Style any h1, h2 elements in the form */
.card h1,
.card h2 {
	text-align: center !important;
	color: #1f2937 !important;
	font-weight: 700 !important;
	margin: 80px 0 15px 0 !important;
	font-size: 2.2em !important;
	position: relative;
	z-index: 5;
}

/* Style paragraphs and description text */
.card p {
	text-align: center !important;
	color: #6b7280 !important;
	font-size: 1.1em !important;
	margin-bottom: 30px !important;
	line-height: 1.5 !important;
	position: relative;
	z-index: 5;
}

/* Style the inputs wrapper */
.inputs-wrapper {
	position: relative;
	z-index: 5;
}

/* Style all input fields */
.card input[type="text"],
.card input[type="email"],
.card input[type="tel"],
.card input[type="number"],
.card textarea,
.card select {
	width: 100% !important;
	padding: 12px 16px !important;
	border: 2px solid #e5e7eb !important;
	border-radius: 8px !important;
	font-size: 16px !important;
	transition: border-color 0.3s ease !important;
	margin-bottom: 15px !important;
	box-sizing: border-box !important;
}

.card input[type="text"]:focus,
.card input[type="email"]:focus,
.card input[type="tel"]:focus,
.card input[type="number"]:focus,
.card textarea:focus,
.card select:focus {
	outline: none !important;
	border-color: #6366f1 !important;
	box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.1) !important;
}

/* Style the submit button */
.card button[type="submit"],
.card input[type="submit"] {
	background: linear-gradient(135deg, #ff6b6b 0%, #ff8e8e 100%) !important;
	color: white !important;
	border: none !important;
	padding: 15px 40px !important;
	border-radius: 10px !important;
	font-size: 18px !important;
	font-weight: 600 !important;
	cursor: pointer !important;
	width: 100% !important;
	margin-top: 20px !important;
	transition: all 0.3s ease !important;
	box-shadow: 0 4px 15px rgba(255, 107, 107, 0.3) !important;
	position: relative;
	z-index: 5;
}

.card button[type="submit"]:hover,
.card input[type="submit"]:hover {
	transform: translateY(-2px) !important;
	box-shadow: 0 6px 20px rgba(255, 107, 107, 0.4) !important;
}

/* Style the body to complement the form */
body.vsc-initialized {
	background: #f8fafc !important;
	font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif !important;
	margin: 0 !important;
	padding: 20px !important;
}

/* Style the container div */
.container {
	max-width: 600px !important;
	margin: 0 auto !important;
}

/* Mobile responsive */
@media (max-width: 768px) {
	.card {
		margin: 10px !important;
		padding: 15px !important;
	}

	.card:before {
		height: 50px !important;
		margin: -15px -15px 20px -15px !important;
	}

	.card:after {
		top: 35px !important;
		font-size: 35px !important;
	}

	.form-header,
	.card h1,
	.card h2 {
		font-size: 1.8em !important;
		margin: 60px 0 15px 0 !important;
	}
}

