let allTitles = [];
let allDescriptions = [];
let allKeywords = [];

for (const item of $json.items) {
  allTitles.push(item.title);
  allDescriptions.push(item.snippet);

  const keywords = item.title
    .toLowerCase()
    .replace(/[^a-zA-Z0-9 ]/g, '')
    .split(' ')
    .filter(w => w.length > 3);

  allKeywords.push(...keywords);
}

// Create unique keywords
const uniqueKeywords = [...new Set(allKeywords)];

return [
  {
    json: {
      chatInput: `
Title List:
${allTitles.join('\n')}

Description List:
${allDescriptions.join('\n')}

Keywords:
${uniqueKeywords.join(', ')}
      `.trim()
    }
  }
];

