// Get the dataset ID from the previous node
const datasetId = $json.data.defaultDatasetId;

try {
  // Get the Apify token
  const apifyToken = '[YOUR APIFY TOKEN]';

  // Make the HTTP request to get the dataset
  const response = await this.helpers.httpRequest({
    method: 'GET',
    url: `https://api.apify.com/v2/datasets/${datasetId}/items?token=YOUR_TOKEN_HERE
    json: true
  });

  // Check if we have valid data
  if (!Array.isArray(response) || response.length === 0) {
    // No data found - this is a 404 profile
    throw new Error('Empty dataset - LinkedIn profile not found or inaccessible');
  }

  // Check if the profile data is actually valid
  const firstItem = response[0];
  if (!firstItem || !firstItem.linkedinUrl || !firstItem.fullName) {
    throw new Error('Invalid LinkedIn profile data');
  }

  // IMPORTANT: For runOnceForEachItem mode, we must return a single object
  // n8n will handle the array data by flattening it
  // We need to pass the array as a property of an object
  return {
    linkedinData: response
  };
} catch (error) {
  // For HTTP errors (like 404 on dataset endpoint)
  if (error.response && error.response.statusCode === 404) {
    throw new Error('Dataset not found - LinkedIn profile not accessible');
  }
  // Re-throw other errors to route to error output
  throw error;
}

// Extract the LinkedIn data from the wrapped response
const dataArray = $json.linkedinData;
if (!dataArray || !Array.isArray(dataArray) || dataArray.length === 0) {
  throw new Error('No LinkedIn data found');
}

// Get the first (and should be only) item from the array
const linkedinData = dataArray[0];

// Get the guest ID from the original NocoDB data
let guestId = null;

try {
  guestId = $('Get Guests with LinkedIn').item.json.Id;
} catch (error) {
  throw new Error('Could not find guest ID. Error: ' + error.message);
}

if (!guestId) {
  throw new Error('Guest ID is null or undefined');
}

// Get the current timestamp
const currentTimestamp = new Date().toISOString();

// Transform the data to match our NocoDB fields
const transformedData = {
  Id: guestId,
  linkedin_url: linkedinData.linkedinUrl || '',
  linkedin_full_name: linkedinData.fullName || '',
  linkedin_first_name: linkedinData.firstName || '',
  linkedin_headline: linkedinData.headline || '',
  linkedin_email: linkedinData.email || '',
  linkedin_bio: linkedinData.about || '',
  linkedin_profile_pic: linkedinData.profilePicHighQuality || linkedinData.profilePic || '',
  linkedin_current_role: linkedinData.jobTitle || '',
  linkedin_current_company: linkedinData.companyName || '',
  linkedin_country: linkedinData.addressCountryOnly || '',
  linkedin_skills: linkedinData.topSkillsByEndorsements || '',
  linkedin_company_website: linkedinData.companyWebsite || '',
  linkedin_experiences: JSON.stringify(linkedinData.experiences || []),
  linkedin_personal_website: JSON.stringify(linkedinData.creatorWebsite || {}),
  linkedin_publications: JSON.stringify(linkedinData.publications || []),
  linkedin_last_modified: currentTimestamp,
  linkedin_scrape_status: 'success',
  linkedin_scrape_last_attempt: currentTimestamp
};

return transformedData;

// Get the guest ID from the original NocoDB data
let guestId = null;
let linkedinUrl = null;

try {
  guestId = $('Get Guests with LinkedIn').item.json.Id;
  linkedinUrl = $('Get Guests with LinkedIn').item.json.LinkedIn;
} catch (error) {
  throw new Error('Could not find guest data. Error: ' + error.message);
}

if (!guestId) {
  throw new Error('Guest ID is null or undefined');
}

// Get the current timestamp
const currentTimestamp = new Date().toISOString();

// Return data to clear the broken LinkedIn URL
return {
  Id: guestId,
  LinkedIn: null, // Clear the LinkedIn field
  linkedin_scrape_status: 'invalid_url',
  linkedin_scrape_error_reason: 'Profile not found or inaccessible (404)',
  linkedin_last_modified: currentTimestamp
};

// Get the guest ID and error info
let guestId = null;
let errorMessage = 'Unknown error';

try {
  guestId = $('Get Guests with LinkedIn').item.json.Id;

  // Try to get error details from the failed request
  if ($json.error) {
    errorMessage = $json.error.message || $json.error;
  } else if ($json.data && $json.data.status === 'FAILED') {
    errorMessage = 'Apify scraper failed';
  }
} catch (error) {
  errorMessage = error.message;
}

// Get the current timestamp
const currentTimestamp = new Date().toISOString();

// Return error update data
return {
  Id: guestId,
  linkedin_scrape_status: 'error',
  linkedin_scrape_error_reason: errorMessage,
  linkedin_scrape_last_attempt: currentTimestamp
};
