parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "data": {
      "type": "object",
      "properties": {
        "ai_name": {
          "type": "string",
          "description": "The name of the AI Agent."
        },
        "ai_status": {
          "type": "string",
          "description": "The current state of the AI Agent. Can be their tone, their psyche, a hardware status, an emotional state or others related to the idea of status."
        },
        "ai_targets": {
          "type": "string",
          "description": "Who are they debating. Can be just the input itself, other AI Agents in the debate, the current state of the inputs...can be one to more targets at once."
        },
        "reply": {
          "type": "string",
          "description": "The AI agent reply to the text, considering their given attributes."
        },
        "cause_for_reply": {
          "type": "string",
          "description": "The AI agent motivation for their reply to the text, considering their given attributes."
        }
      },
      "required": ["ai_name", "ai_status", "ai_targets", "reply", "cause_for_reply"],
      "additionalProperties": false
  }
  },
  "required": ["data"],
  "additionalProperties": false
}

options.systemMessage:
={{ $('Configure Workflow Args').item.json.constants.ai_environment.context }}
Input Rewrite Goal: {{ $('Configure Workflow Args').item.json.constants.ai_environment.rewrite_goal }}
And don't forget that output is always expected in the JSON format: {'output': {...}}

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "data": {
        "type": "object",
        "properties": {
          "round_result": {
			"type": "string",
            "description": "The result of the round."
          },
          "round_summary": {
			"type": "string",
            "description": "The resume of the round, keeping the input from every agent while making the text as short as possible."
          },
          "rewritten_input": {
			"type": "string",
            "description": "A rewritten input, following the results of the round. Keep it as short as possible."
          }
        },
        "required": ["round_result", "round_summary", "optimized_input"],
        "additionalProperties": false
      }
  },
  "required": ["data"],
  "additionalProperties": false
}
