// 1) We get the raw text with $input.first() (direct JS instead of Expression)
// $input provides access to each item as n8n's global variable.
const raw = $input.first().json.text;

// 2) Regex to catch ```json ‚Ä¶ ``` block
const match = raw.match(/```json\s*([\s\S]*?)```/i);

// 3) Extract block or use full text
const jsonString = match ? match[1] : raw;

// 4) Trim + parse
const clean = jsonString.trim();
let parsed;
try {
  parsed = JSON.parse(clean);
} catch (e) {
  throw new Error(`JSON parse hatasƒ±: ${e.message}\n\nRaw i√ßerik:\n${clean}`);
}

// 5) Return a single item output
return [{ json: parsed }];

