// Get all input items from Google Sheets
const items = $input.all();
const alerts = [];
const lowStockItems = [];
const expiryItems = [];

for (const item of items) {
  const data = item.json;

  // Skip header row or empty entries
  if (!data.medicine_name || data.medicine_name === 'medicine_name') {
    continue;
  }

  // Stock threshold (default 10 units if not specified)
  const threshold = data.threshold || 10;

  // Expiry warning in days (30 days before expiry)
  const expiryWarningDays = 30;

  // Check Low Stock
  if (data.stock_quantity && parseInt(data.stock_quantity) <= threshold) {
    const lowStockAlert = {
      medicine_name: data.medicine_name,
      stock_quantity: data.stock_quantity,
      threshold: threshold,
      type: "Low Stock",
      alert_message: `LOW STOCK: ${data.medicine_name} has only ${data.stock_quantity} units remaining (threshold: ${threshold})`
    };

    alerts.push(lowStockAlert);
    lowStockItems.push(`â€¢ ${data.medicine_name}: ${data.stock_quantity} units (threshold: ${threshold})`);
  }

  // Check Expiry Date
  if (data.expiry_date) {
    const today = new Date();
    const expiryDate = new Date(data.expiry_date);
    const daysLeft = Math.ceil((expiryDate - today) / (1000 * 60 * 60 * 24));

    if (daysLeft <= expiryWarningDays) {
      const expiryAlert = {
        medicine_name: data.medicine_name,
        expiry_date: data.expiry_date,
        days_left: daysLeft,
        type: daysLeft > 0 ? "Near Expiry" : "Expired",
        alert_message: daysLeft > 0
          ? `NEAR EXPIRY: ${data.medicine_name} expires in ${daysLeft} days (${data.expiry_date})`
          : `EXPIRED: ${data.medicine_name} expired ${Math.abs(daysLeft)} days ago (${data.expiry_date})`
      };

      alerts.push(expiryAlert);
      expiryItems.push(daysLeft > 0 
        ? `â€¢ ${data.medicine_name}: Expires in ${daysLeft} days (${data.expiry_date})`
        : `â€¢ ${data.medicine_name}: EXPIRED ${Math.abs(daysLeft)} days ago (${data.expiry_date})`);
    }
  }
}

// Create summary for email
const emailSummary = {
  lowStockList: lowStockItems.length > 0 ? lowStockItems.join('\n') : 'No low stock items found.',
  expiryList: expiryItems.length > 0 ? expiryItems.join('\n') : 'No items near expiry or expired.',
  totalAlerts: alerts.length,
  timestamp: new Date().toISOString()
};

// Return all alerts with email summary
if (alerts.length > 0) {
  return [{ json: emailSummary }, ...alerts.map(alert => ({ json: alert }))];
} else {
  // Return empty summary if no alerts
  return [{ json: emailSummary }];
}
