// Minimal formatter: merged transcript turns with per-line timestamps

const b = $json.body ?? $json ?? {};
const toStr = (v) => (typeof v === 'string' ? v.trim() : (v ?? '') + '');
const tsToSec = (ts) => {
  const m = typeof ts === 'string' && ts.match(/^(\d{2}):(\d{2}):(\d{2})$/);
  if (!m) return 0;
  const [, hh, mm, ss] = m;
  return (+hh) * 3600 + (+mm) * 60 + (+ss);
};
const uniqBy = (arr, keyFn) => {
  const seen = new Set();
  return arr.filter(x => {
    const key = keyFn(x);
    if (seen.has(key)) return false;
    seen.add(key);
    return true;
  });
};

// ---- Attendees (optional) ----
const invitees = Array.isArray(b.calendar_invitees) ? b.calendar_invitees : [];
const attendeesRaw = [
  ...invitees.map(i => ({
    name: toStr(i.name) || toStr(i.matched_speaker_display_name) || 'Unknown',
    email: toStr(i.email) || null,
    role: 'Attendee',
  })),
  ...(b.recorded_by ? [{
    name: toStr(b.recorded_by.name) || 'Unknown',
    email: toStr(b.recorded_by.email) || null,
    role: 'Host',
  }] : []),
];
const attendees = uniqBy(attendeesRaw, a => a.email ? `e:${a.email.toLowerCase()}` : `n:${a.name.toLowerCase()}`);

// ---- Per-line → normalize + sort ----
const tItems = Array.isArray(b.transcript) ? b.transcript : [];
const lines = tItems
  .map(i => ({
    timestamp: toStr(i?.timestamp) || '00:00:00',
    seconds: tsToSec(toStr(i?.timestamp)),
    speaker: toStr(i?.speaker?.display_name) || 'Unknown',
    email: toStr(i?.speaker?.matched_calendar_invitee_email) || null,
    text: toStr(i?.text),
  }))
  .filter(x => x.text)
  .sort((a, c) => a.seconds - c.seconds)
  .map(({ seconds, ...rest }) => rest);

// ---- Merge consecutive lines by the same person, but keep per-line timestamps ----
const norm = s => (s || '').trim().replace(/\s+/g, ' ').toLowerCase();
const samePerson = (a, b) => {
  const ae = (a.email || '').toLowerCase();
  const be = (b.email || '').toLowerCase();
  if (ae && be) return ae === be;
  const an = norm(a.speaker);
  const bn = norm(b.speaker);
  if (!ae && !be && (an === 'unknown' || bn === 'unknown')) return false;
  return an === bn;
};

const transcript_merged = [];
for (const line of lines) {
  const last = transcript_merged[transcript_merged.length - 1];
  const entry = { ts: line.timestamp, text: line.text.trim() };

  if (last && samePerson(last, line)) {
    last.lines.push(entry);                 // keep per-line timestamps
    last.texts.push(entry.text);            // backwards-compat (array of strings)
    last.end_timestamp = line.timestamp;
  } else {
    transcript_merged.push({
      speaker: line.speaker,
      email: line.email || null,
      start_timestamp: line.timestamp,
      end_timestamp: line.timestamp,
      start_seconds: tsToSec(line.timestamp),
      lines: [entry],                       // <-- preferred for HTML rendering
      texts: [entry.text],                  // <-- legacy compatibility
    });
  }
}

// ---- Pretty text (one line per turn; keeps the start ts for the turn) ----
const SENTENCE_SEP = '  ';
const transcript_text = transcript_merged
  .map(m => `[${m.start_timestamp}] ${m.speaker}${m.email ? ` <${m.email}>` : ''}: ${m.lines.map(x => x.text).join(SENTENCE_SEP)}`)
  .join('\n');

// ---- Output ----
return {
  json: {
    meeting_title: toStr(b.meeting_title) || toStr(b.title) || null,
    attendees,
    scheduled_start_time: toStr(b.scheduled_start_time) || null,
    scheduled_end_time: toStr(b.scheduled_end_time) || null,
    recording_start_time: toStr(b.recording_start_time) || null,
    recording_end_time: toStr(b.recording_end_time) || null,
    recording_url: toStr(b.share_url) || toStr(b.url) || null,
    transcript_merged,          // now includes lines[{ts,text}] + texts[]
    transcript_text,            // still available if you need it
    // summary_markdown: toStr(b?.default_summary?.markdown_formatted) || null, // optional
  }
};

// --- helpers ---
const esc = (s='') => s.replace(/[&<>]/g, c => ({'&':'&amp;','<':'&lt;','>':'&gt;'}[c]));
const nl2li = s => s.split('\n').map(x=>`<li>${esc(x.replace(/^-+\s?/, ''))}</li>`).join('');
const chunk = (arr, n=3) => arr.reduce((a,_,i)=>(i%n?a[a.length-1].push(arr[i]):a.push([arr[i]]),a),[]);

// --- inputs ---
const o = $json.output; // summary sections
const turns = ($node["Set Fields"]?.json?.transcript_merged) || $json.transcript_merged || [];

// --- transcript (speaker change + every 3 sentences; timestamp from first sentence in chunk) ---
let transcriptHtml = '—';
if (Array.isArray(turns) && turns.length) {
  transcriptHtml = turns.map(t => {
    const parts = chunk((t.lines || []).filter(x => x && x.text), 3);
    return parts.map(group => {
      const stamp = group[0]?.ts || t.start_timestamp;
      const text = group.map(x => (x.text || '').trim()).join(' ');
      // email intentionally omitted
      return `<strong>[${esc(stamp)}] ${esc(t.speaker)}:</strong> ${esc(text)}<br>`;
    }).join('');
  }).join('\n');
}

// --- HTML (H2/H3/H4; compact, readable) ---
const html = `
  <h2>${esc(o["Meeting Title"])}</h2><br>

  <h3>Scheduled Time</h3><p>${esc(o["Scheduled Time"])}</p><br>
  <h3>Recording Time</h3><p>${esc(o["Recording Time"])}</p><br>
  <h3>Recording URL</h3><p>${o["Recording URL"] === "None identified in the transcript" ? "—" : `<a href="${esc(o["Recording URL"])}">${esc(o["Recording URL"])}</a>`}</p><br>

  <h3>Attendees</h3><ul>${nl2li(o["Attendees"])}</ul><br>
  <h3>Executive Summary</h3><p>${esc(o["Executive Summary"])}</p><br>
  <h3>Key Points</h3><ul>${nl2li(o["Key Points"])}</ul><br>
  <h3>Action Items</h3><ul>${nl2li(o["Action Items"])}</ul><br>
  <h3>Decisions</h3><ul>${nl2li(o["Decisions"])}</ul><br>
  <h3>Risks/Concerns</h3><ul>${nl2li(o["Risks/Concerns"])}</ul><br>
  <h3>Open Questions</h3><ul>${nl2li(o["Open Questions"])}</ul><br>

  <h3>Entities</h3>
  <h4>People</h4><ul>${nl2li(o["Entities — People"])}</ul>
  <h4>Orgs/Places</h4><ul>${nl2li(o["Entities — Orgs/Places"])}</ul>
  <h4>Numbers/Dates</h4><ul>${nl2li(o["Entities — Numbers/Dates"])}</ul><br>

  <br><br><hr><br>
  <h3>Full Transcript</h3>
  ${transcriptHtml}
`.trim();

// -> Binary for Drive Upload (Convert to Google Doc = ON)
const filename = `${o["Meeting Title"]} - Meeting Notes.html`;
const buf = Buffer.from(html, 'utf8');
const data = await this.helpers.prepareBinaryData(buf, filename, 'text/html');
return [{ json: { filename }, binary: { data } }];
