/**
 * Robust parser for different upstream shapes:
 * - n8n AI Agent:        item.json.output (string)
 * - OpenAI Chat (HTTP):  item.json.body.choices[0].message.content
 * - OpenAI Chat (direct):item.json.choices[0].message.content
 * - Already an object:   item.json (if the LLM node already parsed JSON)
 */
const outputs = [];

for (const item of items) {
  let content;   // string with JSON
  let data;      // parsed object

  // 1) Try common shapes
  if (typeof item.json?.output === 'string') {
    // n8n "AI Agent" node output
    content = item.json.output;
  } else if (typeof item.json?.choices?.[0]?.message?.content === 'string') {
    // direct Chat Completions shape
    content = item.json.choices[0].message.content;
  } else if (typeof item.json?.body?.choices?.[0]?.message?.content === 'string') {
    // HTTP Request node -> body -> choices
    content = item.json.body.choices[0].message.content;
  } else if (typeof item.json === 'string') {
    // just a raw string
    content = item.json;
  } else if (typeof item.json === 'object' && item.json !== null && (
             item.json.summary || item.json.tasks || item.json.decisions || item.json.notes)) {
    // already parsed into an object with expected keys
    data = item.json;
  }

  // 2) If we still need to parse, do it
  if (!data) {
    if (typeof content !== 'string') {
      throw new Error('Could not locate LLM text output in upstream node. Check: output, choices[0].message.content, or body.choices...');
    }
    try {
      data = JSON.parse(content);
    } catch {
      throw new Error('LLM did not return valid JSON. Verify the system prompt and response_format=json_object.');
    }
  }

  // 3) Normalize the schema
  if (typeof data.summary !== 'string') data.summary = '';
  if (!Array.isArray(data.decisions)) data.decisions = [];
  if (!Array.isArray(data.notes)) data.notes = [];
  if (!['positive','neutral','negative'].includes(data.meeting_sentiment)) {
    data.meeting_sentiment = 'neutral';
  }
  if (!Array.isArray(data.tasks)) data.tasks = [];

  data.tasks = data.tasks.map(t => ({
    description: t?.description ?? '',
    owner: t?.owner ?? 'TBD',
    deadline: t?.deadline ?? 'TBD',
    sentiment: ['positive','neutral','negative'].includes(t?.sentiment) ? t.sentiment : 'neutral'
  }));

  // Optional grouped and totals
  const grouped = { positive: [], neutral: [], negative: [] };
  for (const t of data.tasks) grouped[t.sentiment].push(t);
  data.tasks_grouped = grouped;
  data.totals = {
    positive: grouped.positive.length,
    neutral: grouped.neutral.length,
    negative: grouped.negative.length
  };

  outputs.push({ json: data });
}

return outputs;

// This Code node expects your previous node to output ONE item
// with keys: summary, decisions[], notes[], meeting_sentiment,
// tasks_grouped{positive[],neutral[],negative[]}, totals{...}.

// helper funcs
const esc = (s) => String(s ?? '')
  .replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;')
  .replace(/"/g,'&quot;').replace(/'/g,'&#39;');

const li = (s) => `<li>${esc(s)}</li>`;
const liTask = (t) =>
  `<li><strong>${esc(t.description)}</strong> ‚Äî ${esc(t.owner)} <em>(Deadline: ${esc(t.deadline)})</em></li>`;

// read current item
const d = $json;

// fallbacks if any section is missing
const decisions = Array.isArray(d.decisions) ? d.decisions : [];
const notes     = Array.isArray(d.notes) ? d.notes : [];
const tg        = d.tasks_grouped || { positive: [], neutral: [], negative: [] };
const totals    = d.totals || {
  positive: tg.positive?.length ?? 0,
  neutral:  tg.neutral?.length  ?? 0,
  negative: tg.negative?.length ?? 0
};

// build section HTML safely
const decisionsHtml = decisions.map(li).join('');
const notesHtml     = notes.map(li).join('');
const posHtml       = (tg.positive || []).map(liTask).join('');
const neuHtml       = (tg.neutral  || []).map(liTask).join('');
const negHtml       = (tg.negative || []).map(liTask).join('');

// MINIMAL, COMPATIBLE HTML (no handlebars, no fancy CSS needed)
const html = `<!DOCTYPE html>
<html>
  <body style="font-family: Arial, Helvetica, sans-serif; color:#1f2937; line-height:1.55; margin:0; padding:16px;">
    <h2 style="margin:0 0 8px 0;">üìù Meeting Summary</h2>
    <p style="margin:0 0 8px 0;">${esc(d.summary || '')}</p>

    <p style="margin:8px 0;">
       <strong>Positive:</strong> ${totals.positive}
      &nbsp;‚Ä¢&nbsp; <strong>Neutral:</strong> ${totals.neutral}
      &nbsp;‚Ä¢&nbsp; <strong>Negative:</strong> ${totals.negative}
    </p>

    ${decisionsHtml ? `
      <h3 style="margin:16px 0 6px;">üí° Key Decisions</h3>
      <ul style="margin:0 0 12px 18px;">${decisionsHtml}</ul>
    ` : ''}

    ${notesHtml ? `
      <h3 style="margin:16px 0 6px;">üóíÔ∏è Notes</h3>
      <ul style="margin:0 0 12px 18px;">${notesHtml}</ul>
    ` : ''}

    ${posHtml ? `
      <h3 style="margin:16px 0 6px;">‚úÖ Positive Tasks</h3>
      <ul style="margin:0 0 12px 18px;">${posHtml}</ul>
    ` : ''}

    ${neuHtml ? `
      <h3 style="margin:16px 0 6px;">‚ö™ Neutral Tasks</h3>
      <ul style="margin:0 0 12px 18px;">${neuHtml}</ul>
    ` : ''}

    ${negHtml ? `
      <h3 style="margin:16px 0 6px;">‚ùå Negative Tasks</h3>
      <ul style="margin:0 0 12px 18px;">${negHtml}</ul>
    ` : ''}

    <p style="font-size:12px; color:#6b7280; margin-top:16px;">Sent by n8n ‚Ä¢ AI Meeting Assistant</p>
  </body>
</html>`;

// output same data + ready-to-send html
return [{ json: { ...d, email_html: html } }];
