// Extract tracking number from different sources
const items = $input.all();
const output = [];

for (const item of items) {
  let trackingNumber = '';
  let customerEmail = '';
  let customerName = '';

  // Check if from webhook (form submission)
  if (item.json.body) {
    trackingNumber = item.json.body.trackingNumber || item.json.body.orderNumber || '';
    customerEmail = item.json.body.email || '';
    customerName = item.json.body.name || 'Customer';
  }

  // Check if from email
  if (item.json.subject || item.json.text) {
    // Extract tracking number using regex (DHL format)
    const text = item.json.text || item.json.snippet || '';
    const dhlPattern = /\b\d{10,}\b/g;
    const matches = text.match(dhlPattern);

    if (matches && matches.length > 0) {
      trackingNumber = matches[0];
    }

    customerEmail = item.json.from?.value?.[0]?.address || item.json.from || '';
    customerName = item.json.from?.value?.[0]?.name || 'Customer';
  }

  if (trackingNumber) {
    output.push({
      json: {
        trackingNumber: trackingNumber,
        customerEmail: customerEmail,
        customerName: customerName,
        originalData: item.json
      }
    });
  }
}

return output;

// Format the tracking response
const trackingData = $input.item.json;
const customerData = $node["Extract Tracking Number"].json;

let statusMessage = '';
let trackingDetails = {};

try {
  // Parse DHL API response
  const shipment = trackingData.shipments?.[0];

  if (shipment) {
    const latestEvent = shipment.events?.[0];
    const status = shipment.status;

    trackingDetails = {
      trackingNumber: customerData.trackingNumber,
      currentStatus: status?.status || 'In Transit',
      statusDescription: status?.description || '',
      lastUpdate: latestEvent?.timestamp || new Date().toISOString(),
      location: latestEvent?.location?.address?.addressLocality || 'Unknown',
      estimatedDelivery: shipment.estimatedTimeOfDelivery || 'Not available',
      carrier: 'DHL Express'
    };

    statusMessage = `
Dear ${customerData.customerName},

Thank you for your inquiry about tracking number: ${trackingDetails.trackingNumber}

