const { loadEvaluator } = require("langchain/evaluation");

const llm = await this.getInputConnectionData('ai_languageModel', 0);
const inputData = this.getInputData();
const inputSummary = inputData[0].json.response.text;
const baselineSummary = $('Set Baseline').item.json.baseline_summary

const evaluator = await loadEvaluator("labeled_criteria", { criteria: 'helpfulness', llm });

const result = await evaluator.evaluateStrings({
  input: "Summarize Wikipedia article about the dancing plague of 1518",
  prediction: inputSummary,
  reference: baselineSummary,
});


return [ {json: { ...result } } ];
