for (const item of $input.all()) {
  const toPath = $('Set folder-paths for from and to').item.json.to;
  const fromPath = $('Set folder-paths for from and to').item.json.from;

   // Remove leading and trailing slashes
  path = fromPath.replace(/^\/|\/$/g, '');
  // Split the path into an array of folders
  const folders = path.split('/');
  // Remove empty strings (resulting from leading, trailing, or consecutive slashes)
  const nonEmptyFoldersCount = folders.filter(folder => folder !== '').length;

  newFilePathArray = item.json.path.replace(/^\/|\/$/g, '').split('/');

  item.json.newPath = toPath.replace(/^\/|\/$/g, '') + '/' + newFilePathArray.slice(nonEmptyFoldersCount).join("/")
}

return $input.all();

let results = [],
  i = 0;

do {
  try {
    results = results.concat($("Consolidate all files and folders found").all(0, i));
  } catch (error) {
    return results;
  }
  i++;
} while (true);
