let results = [];

const fileInputs = $('Combine Fields').first().json.data.filter(item => item.fieldType === 'file');

if (!fileInputs.length) return [];

const { json, binary } = $('Render Form').first();

for (fileInput of fileInputs) {
  const binaryKeys = Object.keys(binary).filter(key => key.startsWith(fileInput.fieldLabel));
  for (key of binaryKeys) {
    results.push({
      json: { fieldLabel: fileInput.fieldLabel },
      binary: {
          data: binary[key],
      }
    });
  }
}

return results;

function createField (
  label = '',
  type = '',
  options = {},
) {
  return {
    fieldLabel: label,
    fieldType: type,
    formatDate: options.formatDate,
    fieldOptions: options.choices ?  { values: options.choices } : undefined,
    requiredField: options.isRequired || true,
    placeholder: options.placeholder,
    multiselect: options.isMultipleSelect,
    multipleFiles: options.isMultipleFiles,
    acceptFileTypes: options.acceptFileType,
  }
};


const { type, name, options } = $input.item.json;
let field = null;

switch (type) {
  case 'singleLineText':
  case 'phoneNumber':
  case 'url': {
    field = createField(name, 'text');
    break;
  }
  case 'multilineText': {
    field = createField(name, 'textarea');
    break;
  }
  case 'number': {
    field = createField(name, 'number');
    break;
  }
  case 'email': {
    field = createField(name, 'email');
    break;
  }
  case 'dateTime': {
    field = createField(name, 'date', {
      formatDate: `yyyy-MM-dd HH:mm`
    });
    break;
  }
  case 'singleSelect':
  case 'multipleSelects': {
    field = createField(name, 'dropdown', {
      choices: options.choices.map(choice => ({ option: choice.name })),
      isMultipleSelect: type === 'multipleSelects'
    });
    break;
  }
  case 'checkbox': {
    field = createField(name, 'dropdown', {
      choices: [{ 'option': name }],
      isMultipleSelect: true
    });
    break;
  }
  case 'multipleAttachments': {
    field = createField(name, 'file', {
      isMultipleFiles: true,
    });
    break;
  }
  default:
}

return { json: field || {} }

