const pairs = $input.all().map((item, idx) => ({
  field: $('Files To List1').itemMatching(idx).json.fieldLabel,
  file: item.json,
}));

const groups = {};
pairs.forEach(pair => {
  if (!groups[pair.field]) groups[pair.field] = [];
  groups[pair.field].push({
    name: pair.file.name,
    visible_name: pair.file.original_name
  });
});

return { json: { data: groups } };

const schema = $('Baserow List Fields').all().map(input => input.json);
const data = $input.item.json;

// 1. filter out file inputs
const fileKeys = schema.filter(item => item.type === 'file').map(item => item.name);

const filteredData = Object.keys(data)
  .filter(key => !fileKeys.includes(key))
  .reduce((acc,key) => ({
    ...acc,
    [key]: data[key]
  }), {});

// 2. typecast for boolean
const booleanKeys = schema.filter(item => item.type === 'boolean').map(item => item.name);

booleanKeys.forEach(key => {
  if (filteredData[key] !== undefined) filteredData[key] = Boolean(filteredData[key]);
});

return { json: filteredData }

