// JavaScript code to extract YouTube channel ID, username, or video ID from a given input and return in n8n compatible format

// Initialize an array to hold the output items
const items = [];

// Extract the input value from the previous node's output using the $input API
const inputData = $input.all(); // Get all input data
// Assuming 'youtube Channel username or ID' is the correct key, and it's in the first input item
const input = inputData.length > 0 ? inputData[0].json["youtube Channel username or ID"] : null;

// Check if input exists
if (!input) {
    throw new Error('Input is undefined or not provided');
}

// Regular expressions for different YouTube URL and input formats
const usernamePattern = /^@?([a-zA-Z0-9_-]+)$/;
const channelIdPattern = /^(UC[a-zA-Z0-9_-]{22})$/; // Ensure channel ID starts with "UC"
const videoUrlPattern1 = /(?:https?:\/\/)?www\.youtube\.com\/watch\?v=([a-zA-Z0-9_-]+)/;
const videoUrlPattern2 = /(?:https?:\/\/)?youtu\.be\/([a-zA-Z0-9_-]+)/;
const channelUrlPattern1 = /(?:https?:\/\/)?www\.youtube\.com\/@([a-zA-Z0-9_-]+)/;
const channelUrlPattern2 = /(?:https?:\/\/)?www\.youtube\.com\/channel\/(UC[a-zA-Z0-9_-]{22})/;
const customChannelUrlPattern = /(?:https?:\/\/)?www\.youtube\.com\/c\/([a-zA-Z0-9_-]+)/; // Pattern for custom channel URLs

// Function to determine the type and value of the input
function determineTypeAndValue(input) {
    if (channelIdPattern.test(input)) {
        return { type: 'channel ID', value: input };
    } else if (usernamePattern.test(input)) {
        return { type: 'channel username', value: input };
    } else if (videoUrlPattern1.test(input) || videoUrlPattern2.test(input)) {
        const videoId = videoUrlPattern1.test(input) ? input.match(videoUrlPattern1)[1] : input.match(videoUrlPattern2)[1];
        return { type: 'video ID', value: videoId };
    } else if (channelUrlPattern1.test(input) || customChannelUrlPattern.test(input)) {
        const username = channelUrlPattern1.test(input) ? input.match(channelUrlPattern1)[1] : input.match(customChannelUrlPattern)[1];
        return { type: 'channel username', value: username };
    } else if (channelUrlPattern2.test(input)) {
        return { type: 'channel ID', value: input.match(channelUrlPattern2)[1] };
    } else {
        return { error: 'Invalid input or unsupported format.' };
    }
}

// Process the input and add the result to the items array
const result = determineTypeAndValue(input);
items.push({ json: result });

return items; // Return the array of items

// Assuming inputData is dynamically retrieved as follows
const inputData = $item("0").$node["Merga Data of Youtube & Community RSS"].json;

// Initialize HTML with a modern styled table
let html = `
<style>
  table {
    width: 100%;
    border-collapse: collapse;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    box-shadow: 0 2px 15px rgba(0,0,0,0.1);
    border-radius: 8px;
    overflow: hidden;
    margin-top: 20px;
  }
  th, td {
    border: 1px solid #ddd;
    padding: 8px 16px;
    text-align: left;
    color: #333;
  }
  th {
    background-color: #f0f0f0;
    color: #000;
    font-weight: 600;
  }
  tr:nth-child(even) {background-color: #f9f9f9;}
  tr:hover {background-color: #f1f1f1;}
  a {
    text-decoration: none;
    color: #0645AD;
  }
  a:hover {
    text-decoration: underline;
  }
</style>
<table>
<tr>
  <th>Type</th>
  <th>Format</th>
  <th>URL</th>
</tr>`;

// Function to process each item and add it to the HTML table
Object.entries(inputData).forEach(([key, value]) => {
  // Extract type and format from the key, assuming key format 'Category - Format'
  const [type, format] = key.split(' - ');
  html += `<tr>
    <td>${type} RSS</td>
    <td>${format}</td>
    <td><a href="${value}" target="_blank">${value}</a></td>
  </tr>`;
});

// Close the HTML table tag
html += `</table>`;

// Return the HTML string as output
return [{json: {html: html}}];
