assignments.value:
={{
$json.children.flatMap(item => {
  return [
    { id: item.id, story_id: item.story_id, story_title: $json.title, author: item.author, text: item.text },
    ...item.children.flatMap(item1 => {
         return [
           { id: item1.id, story_id: item1.story_id, story_title: $json.title, author: item1.author, text: item1.text },
           ...item1.children.flatMap(item2 => {
               return [
                 { id: item2.id, story_id: item2.story_id, story_title: $json.title, author: item2.author, text: item2.text },
                 ...item2.children.flatMap(item3 => {
                     return [
                       { id: item3.id, story_id: item3.story_id, story_title: $json.title, author: item3.author, text: item3.text },
                       ...item3.children.flatMap(item4 => {
                          return { id: item4.id, story_id: item4.story_id, story_title: $json.title, author: item4.author, text: item4.text }
                       })
                     ]
                 })
               ]
           })
        ]
    })
  ]
})
}}

parameters.jsonOutput:
={{ {
  ...$json.output,
  "Story ID": $('Set Variables1').item.json.story_id,
  "Story Title": $('Get Payload of Points').item.json.result[0].payload.metadata.story_title,
  "Number of Responses": $('Get Payload of Points').item.json.result.length,
  "Raw Responses": $('Get Payload of Points').item.json.result.map(item =>
    [
      item.payload.metadata.item_id,
      item.payload.metadata.story_id,
      item.payload.metadata.story_title,
      item.payload.metadata.item_author,
      item.payload.content.replaceAll('"', '\"').replaceAll('\n', ' ').substring(0, 500)
    ]
   ).join('\n')
} }}

parameters.text:
