// Access all items from the incoming node
const items = $input.all();

// Combine all fields of each item into a single string
const combinedData = items.map(item => {
    const inputData = item.json; // Access the JSON data of the current item

    // Combine all fields into a single string
    const combinedField = Object.entries(inputData)
        .map(([key, value]) => {
            // Handle objects or arrays by converting them to JSON strings
            const formattedValue = typeof value === 'object' ? JSON.stringify(value) : value;
            return `${key}: ${formattedValue}`;
        })
        .join(' | '); // Combine key-value pairs as a single string with a delimiter

    // Return the new structure
    return {
        json: {
            combinedField // Only keep the combined field for table representation
        },
    };
});

// Output the combined data
return combinedData;

// Access the 'data' field from the input
let rawData = $json["data"];

// Split the string by colon (:) to extract parts
let parts = rawData.split(":");

// Create an object with the extracted parts
return {
  upid: parts[0],       // UPID
  node: parts[1],       // Node (e.g., psb1)
  processID: parts[2],  // Process ID
  taskID: parts[3],     // Task ID
  timestamp: parts[4],  // Timestamp
  operation: parts[5],  // Operation (e.g., aptupdate)
  user: parts[7]        // User (e.g., root@pam!n8n)
};

// Extract required fields from the input
let node = $json["node"] || "unknown node";
let operation = $json["operation"] || "unknown operation";
let user = $json["user"] || "unknown user";
let rawTimestamp = $json["timestamp"] || "unknown timestamp";

// Convert timestamp to a readable format
let readableTimestamp = "Invalid timestamp";
try {
  let timestamp = parseInt(rawTimestamp, 16) * 1000; // Convert hex to milliseconds
  readableTimestamp = new Date(timestamp).toLocaleString();
} catch (error) {
  readableTimestamp = "Unable to parse timestamp";
}

// Construct the simple message
let message = `The operation '${operation}' was executed successfully on node '${node}' by user '${user}' at '${readableTimestamp}'.`;

return {
  message: message
};
