let results = [];

for (const item of $input.all()) {
  console.log(item);
  for (const key of Object.keys(item.binary)) {
    results.push({
        json: {},
        binary: {
            data: item.binary[key],
        }
    });
  }
}

return results;

// "items" is the array coming from the previous node (14 items)
const merged = { json: {}, binary: {} };

for (const item of $input.all()) {
  const data = {
    [item.binary.data.fileName]: item.binary.data
  };
  Object.assign(merged.binary, data); // copy every file property
}

return [merged];     // one single item goes out

