for (item of items) {
  // Get the current semester ID
  const currentSemesterID = item.json["semesterID"]
  let allSemesterIDs = [currentSemesterID];

  // Add semesters that the user is already associated with
  if (item.json["Semesters"]?.length > 0) {
    allSemesterIDs = allSemesterIDs.concat(item.json["Semesters"].filter(semesterID => semesterID !== currentSemesterID));
  }

  // Set allSemesterIDs which is used to update the relation
  item.json["allSemesterIDs"] = allSemesterIDs
}

return items;

