const client_id = "57db0bab2932f657";
const client_secret = "edfaba095e9e7ddefe2e960ce2e98345230a016d";

// Combine client_id and client_secret with a colon
const combinedString = client_id+":"+client_secret;

// Encode the combined string in Base64
const encodedString = Buffer.from(combinedString).toString('base64');

// Create the Authorization header value
const authorizationHeader = `Basic ${encodedString}`;

