// Define the base URL for the YouTube Data API
const BASE_URL = 'https://www.googleapis.com/youtube/v3/videos';

// Get all input items
const items = $input.all();

// Process each item and create YouTube URLs
const results = items.map(item => {
    const VIDEO_ID = item.json.VIDEO_ID;
    const GOOGLE_API_KEY = item.json.GOOGLE_API_KEY;

    if (!VIDEO_ID) {
        throw new Error('The video ID parameter is empty.');
    }

    if (!GOOGLE_API_KEY) {
        throw new Error('The Google API Key is missing.');
    }

    // Construct the API URL with the video ID and dynamically retrieved API key
    const youtubeUrl = `${BASE_URL}?part=snippet,contentDetails,status,statistics,player,topicDetails&id=${VIDEO_ID}&key=${GOOGLE_API_KEY}`;

    return {
        json: {
            youtubeUrl: youtubeUrl
        }
    };
});

// Return array of results
return results;

/**
 * Processes YouTube video items and adds recent_videos flag based on publication date
 * @param {Object[]} $input.all() - Array of input items from previous node
 * @param {number} days - Number of days to check for recent videos (default: 3)
 * @returns {Object[]} - Processed video items with additional properties
 */
try {
  // Get all input items from previous node
  const items = $input.all();

  // Define the threshold for recent videos (in days)
  const days = 3;

  // Validate inputs
  if (!Array.isArray(items)) {
    throw new Error('Input must be an array of items');
  }

  // Process each video item
  const videos = items.map((item, index) => {
    try {
      // Validate required properties
      if (!item?.json?.id || !item?.json?.pubDate) {
        throw new Error(`Missing required properties in item ${index}`);
      }

      // Extract YouTube video ID from the full ID string
      const videoId = item.json.id.split(':')[2];

      // Calculate if video is recent based on publication date
      const pubDate = new Date(item.json.pubDate);
      const thresholdDate = DateTime.now()
        .setZone('America/New_York')
        .startOf('day')
        .minus({days: days});

      // Return processed item with additional properties
      return {
        json: {
          ...item.json,
          id: videoId,
          recent_videos: pubDate > new Date(thresholdDate)
        }
      };
    } catch (itemError) {
      // Handle individual item processing errors
      console.error(`Error processing item ${index}:`, itemError.message);
      // Return original item if processing fails
      return item;
    }
  });

  return videos;

} catch (error) {
  // Handle general execution errors
  console.error('Error in code execution:', error.message);
  throw new Error(`Failed to process video items: ${error.message}`);
}
