function parseNextParams(headerValue) {
    // Match the URL inside <>
    const urlMatch = headerValue.match(/<([^>]+)>;\s*rel="next"/);
    if (!urlMatch) return null;

    const url = urlMatch[1]; // Extracted URL
    const paramsString = url.split("?")[1]; // Get query string

    if (!paramsString) return {}; // No params found

    // Convert query string to object
    return paramsString.split("&").reduce((acc, param) => {
        const [key, value] = param.split("=");
        acc[decodeURIComponent(key)] = decodeURIComponent(value);
        return acc;
    }, {});
}

