const rows = $input.all().map(item => item.json.row);
const maxLength = Math.max(...rows.map(row => row.length));

const table = [
  '|' + rows[0].join('|') + '|',
  '|' + Array(maxLength).fill(0).map(_ => '-').join('|') + '|',
  rows.slice(1, rows.length)
    .filter(row => row.some(Boolean))
    .map(row =>
      '|' + row.join('|') + '|'
    ).join('\n')
].join('\n')

return { table }

