                    inputData.audio;

  if (!base64Data) throw new Error('No audio data');

  // à¹à¸›à¸¥à¸‡ base64 à¹€à¸›à¹‡à¸™ buffer
  const audioBuffer = Buffer.from(base64Data, 'base64');

  // à¸•à¸£à¸§à¸ˆà¸ªà¸­à¸šà¸§à¹ˆà¸²à¹€à¸›à¹‡à¸™ WAV à¸­à¸¢à¸¹à¹ˆà¹à¸¥à¹‰à¸§à¸«à¸£à¸·à¸­à¹„à¸¡à¹ˆ (4 bytes à¹à¸£à¸)
  if (audioBuffer.subarray(0, 4).toString() === 'RIFF') {
    // à¹€à¸›à¹‡à¸™ WAV à¸­à¸¢à¸¹à¹ˆà¹à¸¥à¹‰à¸§ à¸ªà¹ˆà¸‡à¸•à¹ˆà¸­à¹€à¸¥à¸¢
    return [{
      json: {
        success: true,
        fileName: 'audio.wav',
        audioData: base64Data,
        mimeType: 'audio/wav',
        note: 'Already WAV format'
      },
      binary: {
        audio: {
          data: base64Data,
          mimeType: 'audio/wav',
          fileName: 'audio.wav'
        }
      }
    }];
  }

  // à¸ªà¸£à¹‰à¸²à¸‡ WAV header à¹à¸šà¸šà¹€à¸£à¹‡à¸§ (44 bytes)
  const dataSize = audioBuffer.length;
  const header = Buffer.from([
    // RIFF header
    0x52, 0x49, 0x46, 0x46, // "RIFF"
    (36 + dataSize) & 0xff, ((36 + dataSize) >> 8) & 0xff, ((36 + dataSize) >> 16) & 0xff, ((36 + dataSize) >> 24) & 0xff,
    0x57, 0x41, 0x56, 0x45, // "WAVE"

    // fmt chunk
    0x66, 0x6d, 0x74, 0x20, // "fmt "
    0x10, 0x00, 0x00, 0x00, // fmt chunk size (16)
    0x01, 0x00,             // PCM format
    0x01, 0x00,             // mono
    0xc0, 0x5d, 0x00, 0x00, // sample rate (24000)
    0x80, 0xbb, 0x00, 0x00, // byte rate
    0x02, 0x00,             // block align
    0x10, 0x00,             // 16 bits

    // data chunk
    0x64, 0x61, 0x74, 0x61, // "data"
    dataSize & 0xff, (dataSize >> 8) & 0xff, (dataSize >> 16) & 0xff, (dataSize >> 24) & 0xff
  ]);

  // à¸£à¸§à¸¡ header + audio data
  const wavFile = Buffer.concat([header, audioBuffer]);
  const wavBase64 = wavFile.toString('base64');

  return [{
    json: {
      success: true,
      fileName: 'audio.wav',
      audioData: wavBase64,
      mimeType: 'audio/wav',
      size: wavFile.length
    },
    binary: {
      audio: {
        data: wavBase64,
        mimeType: 'audio/wav',
        fileName: 'audio.wav'
      }
    }
  }];

} catch (error) {
  return [{ json: { success: false, error: error.message } }];
}

